definition.script:
node('template.nodename') {

        stage('Code Checkout') {
               echo 'Checking out code...'
               checkout([$class: 'GitSCM', branches: [[name: '*/template.branchname']], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'template.gitcredentialsId', url: 'template.git.url']]])
        }

        stage('Code Build') {
                echo 'Building code...'
                sh returnStdout: true, script: '''cd template.servicename
                mvn clean package -DskipTests=true -s settings.xml '''
        }

        stage('Code Tests') {
                echo 'Running test cases for template.servicename ..'
                sh returnStdout: true, script: '''cd template.servicename
                mvn clean test -s settings.xml '''
        }

        stage('Nexus Artifacts upload') {
                echo 'Uploading template.servicename artifacts to nexus..'
                //def pom=readMavenPom file: 'template.servicename/pom.xml'
                //def this_artifact_name="${pom.artifactId}-${pom.version}"
                // echo this_artifact_name
                //nexusArtifactUploader artifacts: [[artifactId: pom.artifactId, classifier: '', file: 'template.servicename/target/template.servicename-0.0.1-SNAPSHOT.zip', type: 'zip']], credentialsId: 'template.nexuscred', groupId: pom.groupId, nexusUrl: 'template.nexusurl', nexusVersion: 'nexus2', protocol: 'http', repository: 'Snapshots', version: pom.version
       }

        stage('Deploy the Mule Proxy') {
                echo 'Deploying Mule Proxy template.servicename ..'
                sh returnStdout: true, script: '''cd template.servicename
                mvn clean deploy -DskipTests=true -s settings.xml '''
        }
}
